myApp.controller("CombatController",["$scope","$http",function(a,b){a.battlefield=function(){b.get("/monsters").then(function(b){a.monster=b.data[0]}),b.get("/players").then(function(b){a.player=b.data[0]})},a.fight=function(){a.outcome=a.monster.monsterHealth-a.player.playerAttack,a.monster.monsterHealth=a.outcome,a.outcome<=0&&alert("You Won!")},a.battlefield()}]);